mapper.insert:
    INSERT INTO DATAROOM (
      	DATA_NO
		,WRITER_NO
		,CATEGORY_NO
		,SECURITY_LEVEL
		,TITLE
		,CONTENT

    )
    VALUES (
        SEQ_DATAROOM_NO.NEXTVAL
        ,#{writerNo}
        ,#{categoryNo}
        ,#{securityLevel}
        ,#{title}
        ,#{content}
    )

    INSERT INTO DATAROOM_FILE (
      	NO
		,DATA_NO
		,FILE_NAME
		,CHANGE_NAME
		,FILE_EXPLAIN

    )
    VALUES (
        SEQ_DATAROOM_FILE_NO.NEXTVAL
        ,SEQ_DATAROOM_NO.CURRVAL
        ,#{fileName}
        ,#{changeName}
        ,#{fileExplain}
    )
